mapper.select:
 select * from t_user	
  
	 AND 1=1
   
        AND id = #{id}
    
   
        AND pwd = #{pwd}

		select usr.*, (select '1' from t_like 
							where targettype = #{targettype} 
							AND userid = #{userid}
							AND targetid = usr.idx ) as likeyn
		from t_user usr
		where us_viewyn = 'Y'
		and idx != #{userid}

		
			
				AND gender = '2'
			
			
				AND gender = '1'
			
			

				AND exists (  
							SELECT 1 from t_like inner
							WHERE
							targettype = 'PROFILE' 
							AND userid = #{userid}
							AND targetid = usr.idx
							)
